svg.script:
// 

	print('#############################################################');
	if( !('width' in this) )
		width = 33;
	if( !('height' in this) )
		height = 21;

	print('#############################################################');
	var center = {
		x	: (width - 1) / 2,
		y	: (height - 1) / 2,
	}

	print('#############################################################');
	if( !('shapeWidth' in this) )
		shapeWidth = 10;
	if( !('shapeHeight' in this) )
		shapeHeight = 10;

	print('#############################################################');
	print(JSON.stringify(shapeHeight));

	shape = {
		rect1	: {
			x		: center.x - shapeWidth / 2,
			y		: center.y - shapeHeight / 2,
			width	: shapeHeight / 3,
			height	: shapeHeight
		},
		rect2	: {
			x		: center.x + shapeWidth / 6,
			y		: center.y - shapeHeight / 2,
			width	: shapeHeight / 3,
			height	: shapeHeight
		}
	};

	print(JSON.stringify(shape));
//
